#include <iostream>
#include <algorithm>
#include <vector>

using namespace std;

vector <int> tree;
int main(int argc, char** argv){
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int N,M,buf;
    long long maxInput=-1,maxHeight=-1;
    long long lo,hi,mid;
    long long sum=0;
    cin>>N>>M;
    for(int i=0; i<N; i++){
        cin>>buf;
        if(buf>maxInput) maxInput = buf;
        tree.push_back(buf);
    }

    // 0 ~ maxInput 까지 b-search
    lo = 0;
    hi = maxInput;
    while(lo<hi){
        sum=0;
        mid = (lo + hi) / 2;
        
        for(int i=0; i<N; i++){
            if(tree[i]>mid){
                sum += tree[i] - mid;
            }
        }
        // cout<<lo<<" "<<mid<<" "<<hi<<" "<<sum<<"\n";
        if(sum>=M){
            maxHeight = mid;
            lo = mid+1;
        }
        else{
            hi = mid;
        }
    }
    cout<<maxHeight;
    // b-search한 값이 M이상이면

    // maxHeight인 값 출력
    return 0;
}
